<!--Footer Ends-->        <script>            $(document).ready(function () {                document.getElementById("home").classList.remove("active");                document.getElementById("contact").classList.remove("active");                document.getElementById("aboutus").classList.remove("active");                document.getElementById("services").classList.add("active");                                              $("#bookingDate").datepicker({                    minDate: 0,                    maxDate: 30,                    dateFormat: "dd/mm/yy",                    changeMonth: true,                    numberOfMonths: 1                });            });            $("#routes").on("change", function () {                getPrice();            });            $("#bookingDate").on("change", function () {                getPrice();            });            function getPrice() {                var bookingDate = $("#bookingDate").val();                var selectedRoute = $("#routes").val();                if (bookingDate != "" && selectedRoute != "") {                    $("#actionLoader").css("display", "block");                    if (csrfHash === '') {                        csrfHash = '6b36fd1ea00758ecee3fb138090e92da';                    }                    $.ajax({                        url: 'http://www.varanasiboatbooking.com/admin/SpecialRates/getNormalOrSpecialRatesForGivenBoatRouteAndDate/',                        data: {boat_id: glob_boat_id, route_id: selectedRoute, bookdate: bookingDate, csrf_token: csrfHash},                        type: 'POST',                        success: function (result) {                            $("#actionLoader").css("display", "none");                            var priceInfo = JSON.parse(result);                            if (priceInfo.price != -1) {                                $("#payment_amt_show").html(priceInfo.price + "/- INR");                                if (priceInfo.special_price_flag != 0) {                                    $("#payment_amt_show").append(" Special Price Applied.");                                }                                $("#bookNowBtn").css("display", "block");                                $("#bookNowBtn").prop("href", "http://www.varanasiboatbooking.com/Booking/bookBoat/" + glob_boat_id);                            } else {                                $("#priceOrBookBtn").html("Get Price");                                $("#payment_amt_show").html(priceInfo.message);                                $("#bookNowBtn").css("display", "none");                            }                            $("#payment_amt_show").css("display", "block");                            csrfHash = priceInfo.csrfHash;                        }                    });                } else {                    alert("Please Select Your Ride Date & Route Both To Get Price.");                }            }            /*Loading Routes*/            function loadRoutes() {                $("#actionLoader").css("display", "block");                if (csrfHash === '') {                    csrfHash = '6b36fd1ea00758ecee3fb138090e92da';                }                $.ajax({                    url: 'http://www.varanasiboatbooking.com/admin/Routes/getAllRoutesOfBoatByBoatIdForMultipleDropDown/',                    data: {boat_id: glob_boat_id, csrf_token: csrfHash},                    type: 'POST',                    success: function (result) {                        var routesInfo = JSON.parse(result);                        $("#actionLoader").css("display", "none");                        $("#routes").html(routesInfo.sizeList);                        csrfHash = routesInfo.csrfHash;                    }                });            }            /*Loading Routes*/            function raiseBoatPriceModal(boat_id) {                $("#priceOrBookBtn").html("Get Price");                glob_boat_id = boat_id;                loadRoutes();                $("#payment_amt_show").html("");                $("#bookingDate").val("");                $("#boatPriceInfoModal").modal('show');            }        </script>        <!-- Boat Price Info Modal -->        <div class="modal fade" id="boatPriceInfoModal" role="dialog" data-backdrop="static" data-keyboard="false">            <div class="modal-dialog modal-md">                <div class="modal-content">                    <div class="modal-header">                        <button type="button" class="close" data-dismiss="modal">&times;</button>                        <h4 class="modal-title">Get Price</h4>                    </div>                    <div class="modal-body" style="max-height: calc(100vh - 160px);overflow-y: auto;">                        <fieldset>                            <legend>Choose Your Date & Route</legend>                            <div class="row">                                <div class="col-sm-1"><b>Ride Date</b></div>                                <div class="col-sm-5">                                    <input type="text" id="bookingDate" autocomplete="off" name="bookingDate" class="form-control" placeholder="Choose Your Date">                                </div>                                <div class="col-sm-1"><b>Route</b></div>                                <div class="col-sm-5">                                    <select id="routes" name="boat_route" id="boat_route" class="form-control">                                    </select>                                </div>                            </div>                            <br>                            <div class="row">                                <div class="col-sm-12">                                    <b><span id="payment_amt_show" style="color:#FF0000;font-size:20px;display:none"></span></b>                                </div>                            </div>                            <br>                            <div class="row">                                <div class="col-sm-6">                                    <button id="priceOrBookBtn" onClick="getPrice();" class="btn btn-sm btn-block btn-primary">Get Price</button>                                </div>                                <div class="col-sm-6">                                    <a id="bookNowBtn" style="display:none;" class="btn btn-sm btn-block btn-success">Book Now</a>                                </div>                            </div>                        </fieldset>                    </div>                        <div class="modal-footer">                        <button class="btn btn-danger" data-dismiss="modal">Close</button>                    </div>                </div>            </div>        </div>        <!-- Boat Price Info Modal -->    </body></html>